class App extends Component {
	constructor() {
		// super() mi serve per usare this.
		super()
		this.state = {
			robots: robots,
			searchfields: ''
		}
	}

// Quando creo un mio methods in react devo usare il seguente 
// modo di scrivere SEMPRE
	onSearchChange = (event) => {
		this.setState({ searchfield: event.target.value})
		const filteredRobots = this.state.robots.filter(robots => {
			return (
				// Attenzione
				// Andrebbe inserito toLowerCase()
				robots.name.includes(this.state.searchfield )
			);
		});
	}


	render() { 
		return (
			<div className='tc'>
				<h1>RoboFriends</h1>
				<SearchBox searchChange={this.onSearchChange}/>
				<CardList robots={this.state.robots} />
			</div>
		);
	}
}

export default App;
