import React from 'react';

const Card = ({name, email, id}) => {
	// posso anche scrivere le props all'interno della funzione
	// const {name, email, id} = props;
	// Questa const mi permette di non usare ogni volta il termine props
	// quando vado a inserire un parametro 
	// {props.name} diventa {name}
	return (
		<div className='tc bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5'>
			<img alt= 'robots' src={`https://robohash.org/${id}?200x200`} />
			<div>
				<h2>{name}</h2>
				<p>{email}</p>
			</div>
		</div>
	);
}


export default Card;
