import React from 'react';
import Card from './Card';
// import {robots} from './robot';
// posso non importare perché lo passo nel componente index.js


// Di fatto voglio creare un loop che passi tutti gli elementi di robots.js
const CardList = ({robots}) => {
	// Causo volontariamente un errore.
	if (true) {
		throw new Error('NOOOOOOOOO')
	}
	// creazione loop
	// uso la funzione .map
	const cardComponent = robots.map((user, i) => {
        	return (
        		<Card 
        		key={i} 
        		id={robots[i].id} 
        		name={robots[i].name} 
        		email={robots[i].email}/> 
        	)
	})
	return (
		<div>  
        	{/*<Card id={robots[0].id} name={robots[0].name} email={robots[0].email}/>
        	<Card id={robots[1].id} name={robots[1].name} email={robots[1].email}/>
        	<Card id={robots[2].id} name={robots[2].name} email={robots[2].email}/>
        	<Card id={robots[3].id} name={robots[3].name} email={robots[3].email}/>*/}
        	{cardComponent}
        {/*Le parentesi graffe indicano che sto usando una funzione di Javascript*/}
        </div>
	);
}

export default CardList;
