		// Siccome é un component é una smart app
		super()
		this.state = {
			// robots: robots,
			robots: [],
			searchfield: ''
		}
	}


componentDidMount() {
	// spiegato successivamente
	fetch('https://jsonplaceholder.typicode.com/users').then(response => {
		response.json();
	})
	.then(users => {
		this.setState({robots: robots})
	});
}




// Quando creo un mio methods in react devo usare il seguente 
// modo di scrivere SEMPRE
	onSearchChange = (event) => {
		this.setState({ searchfield: event.target.value})
	}

// render é una funzione che, sfruttando il virtual DOM, va a creare l'app
	render() { 
		const filteredRobots = this.state.robots.filter(robots => {
			return robots.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
			// Attenzione
			// Andrebbe inserito toLowerCase()
		})
		return (
			<div className='tc'>
				<h1 className= 'f1' >RoboFriends</h1>
				<SearchBox searchChange={this.onSearchChange}/>
				<CardList robots={filteredRobots} />
			</div>
		);
	}
}

export default App;
